#include <iostream>
#include <iomanip>
#include <cstdio>
#include <algorithm>
#include <cmath>

using namespace std;

int main() {
    int c, d, n, m, k;
    cin >> c >> d >> n >> m >> k;
    int t = n * m - k;
    int i;
    int ans = 100000000;
    for (i = 0; i * n <= t; i++) {
        ans = min(ans, (t - i * n) * d + i * c);
    }
    ans = min(ans, i * c);
    cout << ans << endl;
    return 0;
}
