#include <iostream>
#include<cstdio>
#include<cstring>

using namespace std;

int a[300000];
int s[300000];

int main()
{
	int n,m;
	cin >> n >> m;
	memset(s,0,sizeof(s));
	int p = 0;
	int q = 0;
	for (int i = 1; i <= n ; i++)
	{
		scanf("%d", &a[i]);
		//s[i] = s[i-1] + a[i];
		if (a[i] == 1) p++;
		if (a[i] == -1) q++;
	}
	for (int i = 0; i< m; i++)
	{
		int l,r;
		scanf("%d %d", &l, &r);
		int u = r - l + 1;
		if (u% 2==1)
		{
			 printf("0\n");
			 continue;
			}
		u = u / 2;
		if (p >= u && q >= u) printf("1\n"); else printf("0\n");
	}
	//system("pause");
}

