#include<bits/stdc++.h>

using namespace std;

int main()  {
    ios_base::sync_with_stdio(0);
    cin.tie(0); cout.tie(0);

    int itest;
    cin >> itest;

    while (itest--) {
        int n;  cin >> n;

        vector<int> S0;
        vector<int> S1;

        for(int i = 1 ; i <= n ; ++i)    {
            int x;  cin >> x;
            if (x & 1)  S1.push_back(i);
            else        S0.push_back(i);
        }
        if (S0.size())  {
            cout << "1\n";
            cout << S0[0] << "\n";
            continue;
        }
        if (S1.size() > 1)  {
            cout << "2\n";
            cout << S1[0] << " " << S1[1] << "\n";
            continue;
        }
        cout << "-1\n";
    }
}
