#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const int mod=1e9+7;
int n;
ll a[100005];
double ans;
int main(){
	int t;
	cin>>t;
	while(t--){
		cin>>n,ans=-1e18;
		for(int i=1;i<=n;i++)scanf("%lld",&a[i]);
		sort(a+1,a+n+1);
		for(int i=1;i<=n;i++)a[i]+=a[i-1];
		for(int i=1;i<n;i++)ans=max(ans,1.0*a[i]/i+1.0*(a[n]-a[i])/(n-i));
		printf("%.10lf\n",ans);
	}
}
