#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
#define rep(i,a,b) for (int i=a;i<=b;i++)
int n, ans, a[105];
int main()
{
    scanf("%d", &n);
    rep(i, 1, n) scanf("%d", a + i);
    rep(i, 1, n) rep(j, i, n)
    {
        int cnt = 0;
        rep(k, i, j) a[k] ^= 1;
        rep(k, 1, n) cnt += a[k];
        if (cnt > ans) ans = cnt;
        rep(k, i, j) a[k] ^= 1;
    }
    printf("%d\n", ans);
    return 0;
}
