#include <cmath>
#include <cstdio>
#include <string>
#include <vector>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;


string	st;


int	main()
{
//	freopen("A.in", "r", stdin);
//	freopen("A.out", "w", stdout);

	cin >> st;
	int	now = 1, last = -1, most = 0;
	for (int i = 0; i < st.size(); ++ i)
	{
		if (st[i] - '0' == last) now ++; else now = 1;
		most = max(most, now);
		last = st[i] - '0';
	}
	puts(most >= 7 ? "YES" : "NO");

	return 0;
}
