#include <bits/stdc++.h>
using namespace std;

int main() {
	int n;
	int m;
	scanf("%d%d", &n, &m);
	for (int i = 0; i < n; ++i) {
		for (int j = 0; j < m; ++j) {
			if (i % 2 == 0) {
				putchar('#');
			} else {
				int k = i / 2;
				if (k % 2 == 0 && j == m - 1) {
					putchar('#');
				} else if (k % 2 == 1 && j == 0) {
					putchar('#');
				} else {
					putchar('.');
				}
			}
		}
		puts("");
	}
	return 0;
}
