#include<bits/stdc++.h>
using namespace std;
const double eps(1e-8);
const double pi(3.14159265358979);
int n;
long long x;
int main()
{	
	cin>>n>>x;
	char ch;
	long long d;
	int tot=0;
	for(int i=1;i<=n;++i)
	{
		cin>>ch>>d;
		if(ch=='+')
			x+=d;
		else
			if(x<d)
				++tot;
			else
				x-=d;
	}
	cout<<x<<' '<<tot<<endl;
	return 0;
}
