signed main()
{
	ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0);

	int t;
	cin>>t;
	while(t--){
		int n;
		cin>>n;
		vector<int>v(n);
		for(int i=0;i<n;i++) cin>>v[i];
		int idx=n-1;
		while(idx>=0&&v[idx]) idx--;
		if(idx==-1){
			cout<<"0\n";
			continue;
		}
		int l=0;
		while(v[l]) l++;
		cout<<idx-l+2<<'\n';
	}
	return 0;
}
