#include <bits/stdc++.h>

using namespace std;

char a[] = {'A', 'E', 'I', 'O', 'U', 'Y'};

int main()
{
  string s;
  int ans = 1;
  cin >> s;
  int last = -1;
  for(int i=0;i<s.size();i++)
  {
    bool v = false;
    for(int j=0;j<6;j++)
      v |= (s[i] == a[j]);
    if(v)
    {
      ans = max(ans, i - last);
      last = i;
    }
  }
  ans = max(ans, (int)s.size() - last);
  cout << ans;
  return 0;
}
