#include<cstdio>

int n;
int s[5];

int main(){
    s[0]=s[1]=s[2]=0;
    scanf("%d",&n);
    for(int i=0;i<n;i++){
        int v;
        scanf("%d",&v);
        s[i%3]+=v;
    }
    if(s[0]>=s[1]&&s[0]>=s[2])
        printf("chest\n");
    else if(s[1]>=s[0]&&s[1]>=s[2])
        printf("biceps\n");
    else
        printf("back\n");

    return 0;
}
