#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <vector>
#include <iostream>
#include <algorithm>

using namespace std;

#define foreach(e,x) for(__typeof(x.begin()) e=x.begin(); e!=x.end(); ++e)

const int INF = 1000000000;
int l, r;

void solve()
{
	int m;
	char s[100];
	char t[100];
	cin >> m;
	int x;
	int l = -INF - 1, r = INF + 1;
	for(int i = 0; i < m; ++ i) {
		scanf("%s", s);
		cin >> x;
		scanf("%s", t);
		if (s[0] == '>' && s[1] == '=') {
			if (t[0] == 'Y') {
				l = max(l, x);
			} else {
				r = min(r, x - 1);
			}
		}
		if (s[0] == '<' && s[1] == '=') {
			if (t[0] == 'Y') {
				r = min(r, x);
			} else {
				l = max(l, x + 1);
			}
		}
		if (s[0] == '<' && s[1] == 0) {
			if (t[0] == 'Y') {
				r = min(r, x - 1);
			} else {
				l = max(l, x);
			}
		}
		if (s[0] == '>' && s[1] == 0) {
			if (t[0] == 'Y') {
				l = max(l, x + 1);
			} else {
				r = min(r, x);
			}
		}
	}
	if (l > r) {
		cout << "Impossible" << endl;
	} else cout << l << endl;
}

int main()
{
	solve();
	return 0;
}
