#include <bits/stdc++.h>
using namespace std;

void solve() {
    int n;
    cin >> n;
    vector<int> a(n), odd, even;
    for (int i = 0; i < n; i++) {
        cin >> a[i];
        (a[i] % 2 ? odd : even).push_back(i);
    }
    vector<int> ind;
    for (int i = 0; i < 3 && i < odd.size(); i++) {
        ind.push_back(odd[i]);
    }
    for (int i = 0; i < 3 && i < even.size(); i++) {
        ind.push_back(even[i]);
    }
    for (int i : ind) {
        for (int j : ind) {
            for (int k : ind) {
                if (i < j && j < k && (a[i] + a[j] + a[k]) % 2) {
                    cout << "YES\n";
                    cout << i + 1 << " " << j + 1 << " " << k + 1 << "\n";
                    goto done;
                }
            }
        }
    }
    cout << "NO\n";
done:;
}

int main() {
    ios::sync_with_stdio(0), cin.tie(0);
    int T;
    cin >> T;
    while (T--) {
        solve();
    }
    return 0;
}
