#include <cstdio>
#include <cstring>
#include <algorithm>
#include <cmath>
#include <vector>

using namespace std;

int main() {
	int x, y, a, b; scanf("%d%d%d%d", &x, &y, &a, &b);

	vector<pair<int, int> > ans;
	for (int i = a; i <= x; ++i)
		for (int j = b; j <= y; ++j)
			if (i > j) ans.push_back(make_pair(i, j));

	sort(ans.begin(), ans.end());
	printf("%d\n", (int)ans.size());
	for (int i = 0; i < ans.size(); ++i) printf("%d %d\n", ans[i].first, ans[i].second);

	return 0;
}
