#include <iostream>
#include <cstring>
#include <cstdio>
#include <algorithm>

using namespace std;

const int N=100005;

char buff[1000];
int A[1000], n;

int main() {
	scanf("%s", buff);
	n=0;
	for(int i=0; buff[i]; i+=2) A[n++]=buff[i]-'0';
	sort(A, A+n);
	printf("%d", A[0]);
	for(int i=1; i<n; i++) printf("+%d", A[i]);
	printf("\n");
	return 0;
}
