#include <iostream>
using namespace std;
int gcd(int a, int b){
    if(a > b){
        return gcd(b, a);
    }
    if(a==0){
        return b;
    }
    return gcd(b%a, a);
}
int main(){
    int t;cin>>t;
    while(t--){
        int a,b;cin>>a>>b;
        if(gcd(a,b) == 1){
            cout<<"1\n"<<a<<" "<<b<<"\n";
        }
        else{
            cout<<"2\n"<<1<<" "<<b-1<<"\n"<<a<<" "<<b<<"\n";
        }
    }
    return 0;
}
