#include <bits/stdc++.h>
using namespace std;

const int maxn = 1000 + 5;
char s[maxn][maxn];
int a[maxn];

int main() {
    int n, m; scanf("%d%d", &n, &m);
    for (int i = 0; i < n; ++i) scanf("%s", s[i]);
    for (int i = 0; i < m; ++i) scanf("%d", &a[i]);
    long long ans = 0;
    for (int i = 0; i < m; ++i) {
        vector<int> cnt(5, 0);
        for (int j = 0; j < n; ++j) cnt[s[j][i] - 'A']++;
        ans += 1ll * a[i] * *max_element(cnt.begin(), cnt.end());
    }
    printf("%lld\n", ans);
}
