#include<stdio.h>
#include<string.h>
#include<stdlib.h>
#include<vector>
using namespace std;
char s[101],ss[101];
vector<char> ans[2];
int main(){
    scanf("%s%s",s,ss);
    int i;
    for(i=0;s[i]!='|';++i) ans[0].push_back(s[i]);
    for(++i;s[i];++i) ans[1].push_back(s[i]);
    for(i=0;ss[i];++i) ans[ans[0].size()>ans[1].size()].push_back(ss[i]);
    if(ans[0].size()!=ans[1].size()) puts("Impossible");
    else{
         for(i=0;i<ans[0].size();++i) printf("%c",ans[0][i]);
         putchar('|');
         for(i=0;i<ans[0].size();++i) printf("%c",ans[1][i]);
         puts("");
    }
    return 0;
}
