using namespace std;
int T,n;
signed main(){
	ios::sync_with_stdio(false);cin.tie(0);
	cin>>T;
	while(T--){
		cin>>n;
		int f1=0,f2=0;
		for(int i=1;i<=n;++i){
			int x;cin>>x;
			if(x==0)f1=1;
			if(x<0)++f2;
		}
		if(f1){
			cout<<"0\n";
		}
		else if(f2&1){
			cout<<"0\n";
		}
		else{
			cout<<"1\n1 0\n";
		}
	}
	return 0;
}
