using namespace std;

int n;
char s[10000];

int main() {
	freopen("input.txt", "r", stdin);
	freopen("output.txt", "w", stdout);
	scanf("%d", &n);
	scanf("%s", &s);

	for(int i=0; i<n/2; i++) {
		if(s[i] == 'R' && s[i+n/2] == 'L') printf("%d %d\n", i+n/2+1, i+1);
		else printf("%d %d\n", i+1, i+n/2+1);
	}
	return 0;
}
