//No exams!
#include<bits/stdc++.h>
using namespace std;
#define FF first
#define SS second
#define PB push_back 
#define MP make_pair
typedef long long ll;
const ll INF=1<<28;
const ll LINF=1ll<<61;
int n;
int a[11111];
vector<pair<int,int> >v;
int main()
{
	cin>>n;
	int pre=1;
	for(int i=1;i<=n;i++)
	{
		cin>>a[i];
		if(a[i]!=0)
		{
			v.PB(MP(pre,i));
			pre=i+1;
		}
	}
	if(v.size()==0)
	{
		puts("NO");
	}
	else
	{
		puts("YES");
		cout<<v.size()<<endl;
		for(int i=0;i+1<v.size();i++)
		{
			cout<<v[i].FF<<" "<<v[i].SS<<endl;
		}
		cout<<v[v.size()-1].FF<<" "<<n<<endl;
	}
	return 0;
}
