#include <cstdio>
#include <iostream>
#include <algorithm>
#include <vector>
#include <queue>
#include <set>
#include <string>
#include <cstring>

using namespace std;

#define maxn 1000010

char s[maxn];

int main() {
    scanf("%s", s);
    int n = strlen(s), pivot;
    for (int i = 0; i < n; ++i) {
        if (s[i] == '^') {
            pivot = i;
        }
    }
    long long ans = 0;
    for (int i = 0; i < n; ++i) {
        if ('0' <= s[i] && s[i] <= '9') {
            ans += (i - pivot) * (s[i] - '0');
        }
    }
    if (ans == 0) {
        cout << "balance" << endl;
    } else if (ans < 0){
        cout << "left" << endl;
    } else {
        cout << "right" << endl;
    }
    return 0;
}
