inline int read(){
    int x=0;char ch=getchar();
    int f=0;
    while(!isdigit(ch))f|=ch=='-',ch=getchar();
    while(isdigit(ch))x=10*x+ch-'0',ch=getchar();
    return f?-x:x;
}
template<typename T>void print(T x){
    if(x<0)putchar('-'),x=-x;
    if(x>=10)print(x/10);
    putchar(x%10+'0');
}
template<typename T>void print(T x,char let){
    print(x),putchar(let);
}
template<typename T1,typename T2>void ckmin(T1&x,T2 y){
    if(x>y)x=y;
}
template<typename T1,typename T2>void ckmax(T1&x,T2 y){
    if(x<y)x=y;
}

int n;

void solve(){
    n=read();
    int las=-1,die=0,blood=1;
    for(int i=1;i<=n;i++){
        int x=read();
        if(las==0&&x==0){
            die=1;
        }else if(las==1&&x==1){
            blood+=5;
        }else if(x){
            blood++;
        }
        las=x;
    }
    printf("%d\n",die?-1:blood);
}
int main(){
    int T=read();
    while(T--){
        solve();
    }
    return 0;
}
