#include<bits/stdc++.h>
using namespace std;
inline int read()
{
	int sum=0,f=1;
	char ch=getchar();
	while(!isdigit(ch)){if(ch=='-')f*=-1;ch=getchar();}
	while(isdigit(ch)){sum=sum*10+ch-48;ch=getchar();}
	return sum*f;
}
int main()
{
	int T;
	T=read();
	while(T--)
	{
		int n;
		n=read();
		int tot1=0,tot2=0;
		for(int i=1;i<=n;i++)
		{
			int x=read();
			if(x>0) tot1++;
			else tot2++;
		}
		int x=0;
		int a=tot1,b=tot2;		
		for(int i=1;i<=n;i++)
		{
			if(a>0)x++,a--;
			else x--;
			cout<<x<<" ";
		} 
		cout<<endl;
		a=tot1,b=tot2;
		x=0;
		for(int i=1;i<=n;i++)
		{
			if(x==0) a--,x++;
			else
				if(b) b--,x--;
				else a--,x++;
			cout<<x<<" ";
		}
		cout<<endl;
	}
	return 0;
}
