#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;

const int	MAX_N	= 100;
int			N;
char		a		[MAX_N];

void Init()
{
	scanf("%d", &N);
	scanf("%s", a);
}

int Solve()
{
	int ans1 = 0, ans2 = 0;
	for(int i = 0; i < N; ++ i)
		if (a[i] != '4' && a[i] != '7') return 0;
	for(int i = 0; i < N / 2; ++ i) ans1 += a[i] - '0';
	for(int i = N / 2; i < N; ++ i) ans2 += a[i] - '0';
	return ans1 == ans2;
}

int main()
{
	Init();
	if (Solve()) cout << "YES" << endl;
	else cout << "NO" << endl;
	//system("pause");
	return 0;
}
