int main(){
    map<int,int> S;
    S.clear();
    int f[6];
    for(int i=0;i<6;i++){
        scanf("%d",&f[i]);
        S[f[i]]++;
    }
    bool flag=false;
    for(int i=0;i<6;i++)
        if(S[f[i]]>=4){
            S[f[i]]-=4;
            flag=true;
            break;
        }
    if(!flag){
        puts("Alien");
        return 0;
    }
    flag=false;
    for(int i=0;i<6;i++)
        if(S[f[i]]==2)flag=true;
    if(flag)puts("Elephant");
    else puts("Bear");
    return 0;
}
