#include<bits/stdc++.h>
using namespace std;
#define N 23333
typedef long long ll;
int n,a[N],x;
void Solve(){
	cin>>n>>x;
	for(int i=1;i<=n<<1;++i){
		cin>>a[i];
	}
	sort(a+1,a+n+n+1);
	for(int i=1;i<=n;++i){
		if(a[i+n]<a[i]+x){
			cout<<"NO\n";
			return;
		}
	}
	cout<<"YES\n";
}
int main(){
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin>>T;
	while(T--)Solve();
	return 0;
}
