#include <stdio.h>
int n, m, a[100], b[100];
int main()
{
	scanf("%d%d", &n, &m);
	for(int i = 0; i < m; i++)
		scanf("%d", &b[i]);
	for(int i = 0; i < m; i++)
	{
		for(int j = b[i] - 1; j < n; j++)
		{
			if(!a[j])
				a[j] = b[i];
		}
	}
	for(int i = 0; i < n; i++)
		printf("%d ", a[i]);
	getchar(); getchar();
	return 0;
}
