#include<cstdio>
#include<algorithm>
#include<cstring>
#include<cstdlib>
#include<cmath>
#include<iostream>
#include<vector>
using namespace std;

#define maxn 100100
typedef long long ll;
ll a[maxn],b[maxn];
int n;

int main()
{
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
		scanf("%I64d",a+i);

	for(int i=n;i>=1;i--)
	{
		b[i]=a[i]+a[i+1];
	}
	for(int i=1;i<=n;i++)
		printf("%I64d ",b[i]);
	return 0;
}
