#include <bits/stdc++.h>
using namespace std;

const int max_n = 1e2 + 5;

int a[max_n], b[max_n];

void solve_tc(){
	int n; 
	cin >> n;
	for (int i = 1; i <= n; i++){
		cin >> a[i];
	}
	for (int i = 1; i <= n; i++){
		cin >> b[i];
	}

	int max_a = 0, max_b = 0;
	for (int i = 1; i <= n; i++){
		if (a[i] > b[i]){
			swap(a[i], b[i]);
		}
		max_a = max(max_a, a[i]);
		max_b = max(max_b, b[i]);
	}

	cout << max_a * max_b << "\n";
}

int main(){
	ios_base::sync_with_stdio(0);
	cin.tie(0);

	int tests; 
	cin >> tests;
	while (tests--){
		solve_tc();
	}
}
