#include<iostream>
#include<cstdio>
using namespace std;
inline int read()
{
	int x = 0 , f = 1; char ch = getchar();
	while(ch < '0' || ch > '9'){ if(ch == '-') f = -1;  ch = getchar();}
	while(ch >= '0' && ch <= '9'){x = x * 10 + ch - '0';ch = getchar();}
	return x * f;
}
int c,v0,v1,a,l;
int main()
{
	c=read();v0=read();v1=read();a=read();l=read();
	for(int now=1,day=1;;++day)
	{
		int Rd=v0-(day!=1?l:0);
		now+=Rd;
		if(now>c) return 0*printf("%d\n",day);
		v0=min(v1,v0+a);
	}
	return 0;
}
