#include<bits/stdc++.h>

using namespace std;


int main()
{
    int s,t,q;
    cin >> t>> s>> q;
    int ans=1;
    int time=0;
    int ctr=0;
    while(time!= t)
    {
        time++; ctr++;
        if(ctr>1) s++;
        if(ctr==q) ctr=0;
        if(time>s) { ans++;time =1;}
    }
    cout<< ans;
}
