#include<bits/stdc++.h>
#define Rint register int
#define MP make_pair
#define fi first
#define se second
using namespace std;
typedef long long LL;
typedef pair<int, int> pii;
template<typename T>
inline void read(T &x){
	int ch = getchar(); x = 0;
	bool f = false;
	while((ch < '0' || ch > '9') && ch != '-') ch = getchar();
	if(ch == '-'){f = true; ch = getchar();}
	while(ch >= '0' && ch <= '9'){x = x * 10 + ch - '0'; ch = getchar();}
	if(f) x = -x;	
}
int t, n, x, cnt, sum; 
int main(){
	read(t);
	while(t --){
		read(n); cnt = sum = 0;
		for(Rint i = 1;i <= n;++ i) read(x), cnt += !x, sum += x + !x;
		printf("%d\n", cnt + !sum);
	}
}
