#include<bits/stdc++.h>
#define pb push_back
#define eb emplace_back
#define SZ(x) ((int)(x).size())
#define each(x,v) for(auto&x:v)
#define rep(i,a,b) for(int i=(a);i<=(b);++i)
#define per(i,a,b) for(int i=(a);i>=(b);--i)
using namespace std;
int T;
char s[55][55];
int main(){
	scanf("%d",&T);
	while(T--){
		int n,m,r,c;
		scanf("%d%d%d%d",&n,&m,&r,&c);
		int cnt=0;
		rep(i,1,n){
			scanf("%s",s[i]+1);
			rep(j,1,m)cnt+=s[i][j]=='B';
		}
		if(cnt==0){
			puts("-1");
			continue;
		}
		if(s[r][c]=='B'){
			puts("0");
			continue;
		}
		int flg=0;
		rep(i,1,n){
			if(s[i][c]=='B'){
				flg=1;
			}
		}
		rep(j,1,m){
			if(s[r][j]=='B'){
				flg=1;
			}
		}
		if(flg){
			puts("1");
			continue;
		}
		puts("2");
	}
	return 0;
}
