using namespace std;
inline int read()
{
    int x=0,f=1;char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
    while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}
    return x*f;
}
int n,a[105],f[105][2];
int main()
{
    n=read();
    for(int i=1;i<=n;++i) a[i]=read();
    f[0][0]=1;
    for(int i=1;i<=n;++i)
    	for(int j=0;j<i;++j)
    		if((a[j+1]&1)&&(a[i]&1)&&(i-j)&1) f[i][0]|=f[j][1],f[i][1]|=f[j][0];
    puts(f[n][1]?"Yes":"No");
    return 0;
}
