#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
inline int in(){
    int x;
    scanf("%d",&x);
    return x;
}
const int N=1e5+5;
int n,a[N];
void solve(){
    n=in();
    int cnt=0;
    for(int i=1;i<=n;i++)a[i]=in(),cnt+=a[i]==2;
    for(int i=1;i<=n;i++){
        cnt-=(a[i]==2)*2;
        if(cnt==0){cout<<i<<endl;return;}
    }
    cout<<-1<<endl;
}
int main(){
    int T=in();
    while(T--)solve();
    return 0;
}
