#include<bits/stdc++.h>
#define mod 1000000007LL
#define fi first
#define se second
#define mp make_pair
using namespace std;
typedef long long ll;

int n,a,b,d[30],ans;

void en()
{
    puts("-1");
    exit(0);
}

int main()
{
    scanf("%d %d %d",&n,&a,&b);
    //if(a<b) swap(a,b);

    for(int i=0;i<n;i++) scanf("%d",&d[i]);
    for(int i=0;i<n/2;i++)
    {
        if(d[i]==0)
        {
            if(d[n-i-1]==1) en();
            if(d[n-i-1]==2) ans+=a;
        }

        if(d[i]==1)
        {
            if(d[n-i-1]==0) en();
            if(d[n-i-1]==2) ans+=b;
        }

        if(d[i]==2)
        {
            if(d[n-i-1]==0) ans+=a;
            if(d[n-i-1]==1) ans+=b;
            if(d[n-i-1]==2) ans+=2*min(a,b);
        }
    }

    if(n%2==1 && d[n/2]==2) ans+=min(a,b);

    printf("%d",ans);
}
