#include <iostream>
#include <fstream>
#include <vector>
#include <algorithm>
#include <string.h>
#include <assert.h>
#include <string>

using namespace std;

int toInt(string s){
    int h=(s[0]-'0')*10+(s[1]-'0');
    int m=(s[3]-'0')*10+(s[4]-'0');
    return h*60+m;
}

int ispal(int a){
    int h=a/60, m=a%60;
    return (m==(h%10)*10+(h/10));
}

string tostring(int a){
    string s="..:..";
    int h=a/60, m=a%60;
    s[0]=(h/10)+'0';
    s[1]=(h%10)+'0';
    s[3]=(m/10)+'0';
    s[4]=(m%10)+'0';
    return s;
}

int main(){
    string s;
    cin >> s;
    int a=toInt(s);
    for(int i=1; i<24*60; i++){
	int cur=(a+i)%(24*60);
	if(ispal(cur)){
	    cout << tostring(cur) << endl;
	    break;
	}
    }
}
