bool operator < (const string &a, const string &b)
{
    if (a.size() < b.size()) return true;
    if (b.size() < a.size()) return false;

    for (int i = 0;i < a.size();i++)
    {
        if (a[i] < b[i]) return true;
        if (b[i] < a[i]) return false;
    }
    return true;
}

void Solve ()
{
    string b = "127";
    string s = "32767";
    string i = "2147483647";
    string l = "9223372036854775807";

