#include <bits/stdc++.h>
using namespace std;
int t,n;
int main () {
	scanf("%d",&t);
	for (int ii=1;ii<=t;ii++) {
		scanf("%d",&n);
		long long ans1=0,ans2=0;
		for (int i=1;i<=n-1;i++) {
			if (i<=n/2-1) {ans1+=(1ll<<i);}
			else {ans2+=(1ll<<i);}
		}
		ans1+=(1ll<<n);
		printf("%lld\n",ans1-ans2);
	}
	return 0;
}
