using namespace std;

int main() {
  int a, b;
  scanf ("%d %d", &a, &b);
  int x = 0, y = 0, z = 0;
  for (int i = 1; i <= 6; ++i) {
    if (abs(a - i) == abs(b - i)) ++y;
    if (abs(a - i) < abs(b - i)) ++x;
    if (abs(a - i) > abs(b - i)) ++z;
  }
  printf ("%d %d %d\n", x, y, z);
  return 0;
}
