#include <algorithm>
#include <iostream>
#include <cstring>
#include <cstdio>
#include <cmath>
using namespace std;

typedef long long ll;
ll a, b, ans;

int main() {
	cin >> a >> b;
	while (a > 0 && b > 0) {
		ll t = (a - b) / b + 1;
		ans += t;
		a -= t * b;
		swap(a, b);
	}
	cout << ans << endl;
	return 0;
}
