const int MAXN = 105;

int n, m;
pii x[MAXN], s[MAXN];
int c[MAXN], ans[MAXN];

int main() {
    SS(n, m);
    rep (i, 1, n) {
        sf("%d", &x[i].fir);
        x[i].sec = i;
    }
    sort(x + 1, x + 1 + n);
    rep (i, 1, m) sf("%d %d", &s[i].fir, &s[i].sec);
    sort(s + 1, s + 1 + m);
    Fill(c, 0xff);
    rep (i, 1, m) {
        int col = 0;
        rep (j, 1, n) if (s[i].fir <= x[j].fir && x[j].fir <= s[i].sec) {
            if (c[j] == -1) c[j] = col, col ^= 1;
            else col = c[j] ^ 1;
        }
    }
    rep (i, 1, n) {
        if (c[i] == -1) c[i] = 0;
        ans[x[i].sec] = c[i];
    }
    pf("%d", ans[1]);
    rep (i, 2, n) pf(" %d", ans[i]);
    pf("\n");

    return 0;
}
