#include <stdio.h>
#include <string.h>

int main()
{
  //freopen("input.txt", "rt", stdin);
  //freopen("output.txt", "wt", stdout);

  int n;
  int cur = 0;
  int res = 0;
  scanf("%d", &n);
  for (int i = 0; i < n; i++)
  {
    int v;
    scanf("%d", &v);
    cur += v;
    if (cur < 0)
    {
      res++;
      cur = 0;
    }
  }

  printf("%d", res);

  return 0;
}
