#include <bits/stdc++.h>
#define int long long
using namespace std;
int a[1000005];
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		int n;
		cin >> n;
		int cnt=0;
		for(int i=1;i<=n;i++)
		{
			cin >> a[i];
			if(a[i]<0) ++cnt;
			a[i]=max(a[i],-a[i]);
		}
		for(int i=1;i<=n;i++)
			if(i<=cnt) a[i]*=-1;
		int flag=1;
		for(int i=2;i<=n;i++)
			if(a[i]<a[i-1]) flag=0;
		if(flag) cout << "YES\n";
		else cout << "NO\n";
	}
	return 0;
}
