#include<algorithm>
#include<cstring>
#include<cstdio>
#include<cstdlib>
using namespace std;
int n,m,Min,i,j,t,b[100],a[100];
int main(){
	scanf("%d",&n);
	scanf("%d",&m);
	for(i=1;i<=m;i++){
		scanf("%d",&a[i]);
	}
	for(i=1;i<=m;i++){
		for(j=m;j>i;j--){
			if(a[i]>a[j]){ 
			t=a[i];
			a[i]=a[j];
			a[j]=t;
			} 
		}
	}
	for(i=1;i<=m-n+1;i++){
		b[i]=a[i+n-1]-a[i];
	}
	Min=1000;
	for(i=1;i<=m-n+1;i++){
		if(Min>b[i])
		Min=b[i];
	}
	printf("%d",Min);
	return 0;
}
