#include <bits/stdc++.h>
using namespace std;

int a[110];

int main() {
    int n, m;
    scanf("%d%d", &n, &m);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    int tot = 0;
    for (int i = 0; i < n; i++) {
        int j = i, sum = 0;
        for (j = i; j < n; j++) {
            sum += a[j];
            if (sum > m) break;
        }
        if (sum > m) j--;
        tot++;
        i = j;
    }
    printf("%d\n", tot);
    return 0;
}
