#include<bits/stdc++.h>

int main() {
        std::cin.tie(nullptr)->sync_with_stdio(false);

        int t;
        std::cin >> t;
        while (t--) {
                int a, b;
                std::cin >> a >> b;
                std::set<std::pair<int, int>> st;
                st.emplace(std::minmax(a, b));
                if (~a & 1) st.emplace(std::minmax(a / 2, 2 * b));
                if (~b & 1) st.emplace(std::minmax(b / 2, a * 2));
                if (st.size() == 1) std::cout << "No\n";
                else std::cout << "Yes\n";
        }
}
