#include <bits/stdc++.h>
using namespace std;
const int N = 1e5 + 10;
int T, n, a, b;
int main()
{
	ios::sync_with_stdio(0);
	cin >> T;
	while(T--){
		cin >> n >> a >> b;
		int tims = a % (b + 1);
		for(int i = 1; i <= tims; ++i){
			for(int j = 1; j <= (a / (b + 1)) + 1; ++j) cout << "R";
			cout << "B";
		}
		for(int i = tims + 1; i <= b + 1; ++i){
			for(int j = 1; j <= a / (b + 1); ++j) cout << "R";
			if(i != b + 1) cout << "B";
		}
		cout << endl;
	}
	return 0;
}
