#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cstring>
#include<cmath>
#include<queue>
#include<vector>
using namespace std;
#define N 1000002
typedef long long ll;
int n,T;
char a[N],b[N];
int main()
{
	scanf("%d",&T);
	while(T--)
	{
		scanf("%d",&n);
		scanf("%s%s",a+1,b+1);
		int c1=0,c2=0;
		for(int i=1;i<=n;i++)
		{
			if(a[i]-'0'<b[i]-'0')c1++;
			else if(a[i]-'0'>b[i]-'0')c2++;
		}
		if(c1==c2)puts("EQUAL");
		else if(c1<c2)puts("RED");
		else puts("BLUE");
	}
}
